import React, { Component } from 'react'
import { MDBMask, MDBView, MDBContainer, MDBRow, MDBCol } from "mdbreact";
import { demografico } from '../UserFunctions'
import { BoxLoading } from 'react-loadingg';
class Movie extends Component {

    constructor(props) {
        super(props)
        this.state = {
            Id: '',
            movies : null
        }
    }
    componentWillMount() {
        this.setState({ loading: true });
        console.log("Props:",this.props)
        demografico(this.props.id_user).then(
            res => {
                        this.setState({ movies: res});
                        this.setState({ loading: false });
            }
        )

    }

    render() {
        var peliculas = this.state.movies;
        if (this.state.loading == false){
        return (
            peliculas.map( item => (
            <MDBCol md="4">
                <MDBView>
                <img
                    src="https://mdbootstrap.com/img/Others/documentation/forest-sm-mini.jpg"
                    className="img-fluid"
                    alt=""
                />
                <MDBMask className="flex-center" overlay="teal-slight">
                    <p className="white-text">{item.Movie}</p>
                </MDBMask>
                </MDBView>
            </MDBCol>
         ))
        )
    }else{

        return (
            <BoxLoading />
        )
    }
    }
}

export default Movie
